#!/bin/bash\n\n# Check for correct number of arguments\nif [ "$#" -ne 2 ]; then\n    echo "Usage: $0 <path_to_config.yaml> <output_directory_name>"\n    exit 1\nfi\n\nCONFIG_FILE=$1\nOUTPUT_DIR=$2\n\n# Activate the conda environment\nsource activate mergekit\n\n# Run the merge command\nmergekit-yaml $CONFIG_FILE $OUTPUT_DIR --copy-tokenizer --allow-crimes --lazy-unpickle --out-shard-size 5B\n\n# Deactivate the environment\nconda deactivate
